// Ours
const Now = require('../lib')

module.exports = class Scale extends Now {
  getInstances(id) {
    return this.retry((bail, attempt) => __async(function*(){
      if (this._debug) {
        console.time(`> [debug] #${attempt} GET /deployments/${id}/instances`)
      }

      const res = yield this._fetch(`/now/deployments/${id}/instances`, {
        method: 'GET'
      })

      if (this._debug) {
        console.timeEnd(
          `> [debug] #${attempt} GET /deployments/${id}/instances`
        )
      }

      if (res.status === 403) {
        return bail(new Error('Unauthorized'))
      }

      const body = yield res.json()

      if (res.status !== 200) {
        if (res.status === 404 || res.status === 400) {
          const err = new Error(body.error.message)
          err.userError = true
          return bail(err)
        }

        throw new Error(body.error.message)
      }

      return body
    }.call(this)))
  }
}

function __async(g){return new Promise(function(s,j){function c(a,x){try{var r=g[x?"throw":"next"](a)}catch(e){j(e);return}r.done?s(r.value):Promise.resolve(r.value).then(c,d)}function d(e){c(e,1)}c()})}
